import { Button, Card, CardActions, CardContent, CardMedia, Typography } from "@mui/material";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { MovieData } from "../types/movieData";



const Movie = () => {
    let { id } = useParams();
    const [movie, setMovie] = useState<MovieData>({
        id: 99,
        movieName: " ",
        director: " "
    });
    
    useEffect(() => {
        const fetchMovie = async () => {
          const response = await fetch(`http://localhost:8080/api/movies/${id}`);
          const data = await response.json();
          setMovie(data);
        };
    
        fetchMovie();
      }, [])




    return (
        <>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "flex-start", margin: "2rem" }}>
      <div style={{ flex: "1 1 50%", paddingRight: "2rem" }}>
        <Card>
          <CardMedia
            sx={{width:300, objectFit: "cover" }}
            image={movie.coverUrl}
            title={movie.movieName}
          />
        </Card>
      </div>
      <div style={{ flex: "1 1 50%" }}>
        <div style={{ textAlign: "center" }}>
          <Typography variant="h4" component="h1" gutterBottom>
            {movie.movieName}
          </Typography>
        </div>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1rem" }}>
          <Typography variant="subtitle1" color="textSecondary">
            {movie.genre?.genreName}
          </Typography>
          <Typography variant="subtitle1" color="textSecondary">
            {movie.movieLength} min
          </Typography>
        </div>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1rem" }}>
          <Typography variant="subtitle1" color="textSecondary">
            Director: {movie.director}
          </Typography>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Typography variant="subtitle1" color="textSecondary" style={{ marginRight: "0.5rem" }}>
              {movie.moviePoints} stars
            </Typography>
            {Array.from(Array(movie.moviePoints), (_, i) => (
              <svg key={i} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="gold" viewBox="0 0 16 16">
                <path d="M8 12.25l3.299 1.909-.817-3.999L13 6.5l-4.301-.375L8 2.5 6.301 6.125 2 6.5l2.518 2.659-.817 3.999z" />
              </svg>
            ))}
          </div>
        </div>
        <div>
          {/* <Typography variant="body1" gutterBottom>
            Description: {movie.description}
          </Typography> */}
          <Typography variant="h6" gutterBottom>
            Reviews
          </Typography>
          {movie.reviews && movie.reviews.map((review, index) => (
            <div key={index} style={{ marginBottom: "1rem" }}>
              <Typography variant="body1" component="p">
                {review.description}
              </Typography>
              <Typography variant="caption" color="textSecondary" component="p">
                {review.userId} - {review.reviewDate}
              </Typography>
            </div>
          ))}
        </div>
        <CardActions style={{ justifyContent: "flex-end", marginTop: "auto" }}>
          <Button variant="contained" color="primary" style={{ marginRight: "1rem" }}>
            Buy Now
          </Button>
          </CardActions>
          </div>
          </div>
    
        </>
        
    )
}
  
export default Movie;
