import { Container, Grid } from "@mui/material";
import { useEffect, useState } from "react";
import MovieCard from "./MovieCard";
import {MovieData} from '../types/movieData.ts';
const HomePage = () => {
  const [movies, setMovies] = useState<MovieData[]>([]);

  useEffect(() => {
    // Simulate fetching movies from a database
    const fetchedMovies: MovieData[] = [
      {
        id: "1",
        name: "Pulp Fiction",
        genre: "Action",
        coverUrl:
          "https://m.media-amazon.com/images/M/MV5BNGNhMDIzZTUtNTBlZi00MTRlLWFjM2ItYzViMjE3YzI5MjljXkEyXkFqcGdeQXVyNzkwMjQ5NzM@._V1_.jpg",
      },
      {
        id: "2",
        name: "Star Wars",
        genre: "Science Fiction",
        coverUrl:
          "https://m.media-amazon.com/images/I/71Jc8+hEt7L.jpg",
      },
      {
        id: "3",
        name: "Requiem for a Dream",
        genre: "Drama",
        coverUrl: "https://m.media-amazon.com/images/M/MV5BOTdiNzJlOWUtNWMwNS00NmFlLWI0YTEtZmI3YjIzZWUyY2Y3XkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_.jpg",
      },
      
    ];

    setMovies(fetchedMovies);
  }, []);

  return (
    <Container maxWidth="lg" sx={{ flexGrow: 1, paddingTop: 20 }}>
      <Grid container spacing={2}>
        {movies.map((movie) => (
          <Grid item key={movie.id} xs={12} sm={6} md={4}>
            <MovieCard
              movieName={movie.name}
              movieGenre={movie.genre}
              movieCoverUrl={movie.coverUrl}
            />
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default HomePage;
