  //       movieName: "Requiem for a Dream",
  //       director: 'test',
  //       },

  //   ];

  //   setMovies(fetchedMovies);
  // }, []);

  return (
    <Container maxWidth="lg" sx={{ flexGrow: 1, paddingTop: 20 }}>
      <Grid container spacing={2}>
        {movies.map((movie) => (
          <Grid item key={movie.id} xs={12} sm={6} md={4}>
            <MovieCard
              movie={movie}
            />
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default HomePage;
